mapper.select:
SELECT a.appointment_date
FROM appointment a
WHERE a.staff_id = #{staffId}
GROUP BY a.appointment_date
HAVING SUM(CASE WHEN a.appointment_session = 0 THEN 1 ELSE 0 END) > 0
   AND SUM(CASE WHEN a.appointment_session = 1 THEN 1 ELSE 0 END) > 0;

SELECT appointment_session
FROM appointment
WHERE appointment_date = #{appointmentDate} and staff_id = #{staffId}
